import localforage from 'localforage';

// LocalForage konfig√ºrasyonu
localforage.config({
  name: 'cargo-mobile-app',
  storeName: 'offline-queue'
});

export class OfflineQueueService {
  constructor() {
    this.queueKey = 'offline-queue';
    this.syncKey = 'last-sync';
    this.apiBaseUrl = process.env.NODE_ENV === 'development' 
      ? '   http://localhost:3001' 
      : 'http://10.0.2.2:3001';
  }

  // Queue'ya veri ekle
  async addToQueue(data) {
    try {
      const queue = await this.getQueue();
      const queueItem = {
        id: `queue-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
        type: data.type,
        data: data,
        timestamp: new Date().toISOString(),
        deviceId: data.deviceId || 'unknown',
        status: 'pending',
        retryCount: 0,
        maxRetries: 3
      };

      queue.push(queueItem);
      await this.saveQueue(queue);

      console.log('üì± Veri kuyruƒüa eklendi:', queueItem);
      return queueItem.id;
    } catch (error) {
      console.error('‚ùå Queue ekleme hatasƒ±:', error);
      throw error;
    }
  }

  // Queue'dan veri al
  async getQueue() {
    try {
      const queue = await localforage.getItem(this.queueKey);
      return queue || [];
    } catch (error) {
      console.error('‚ùå Queue okuma hatasƒ±:', error);
      return [];
    }
  }

  // Queue'yu kaydet
  async saveQueue(queue) {
    try {
      await localforage.setItem(this.queueKey, queue);
    } catch (error) {
      console.error('‚ùå Queue kaydetme hatasƒ±:', error);
      throw error;
    }
  }

  // Queue uzunluƒüunu al
  async getQueueLength() {
    const queue = await this.getQueue();
    return queue.length;
  }

  // Queue'dan veri kaldƒ±r
  async removeFromQueue(id) {
    try {
      const queue = await this.getQueue();
      const filteredQueue = queue.filter(item => item.id !== id);
      await this.saveQueue(filteredQueue);
      return true;
    } catch (error) {
      console.error('‚ùå Queue silme hatasƒ±:', error);
      return false;
    }
  }

  // Queue'yu sunucu ile senkronize et
  async syncWithServer() {
    try {
      const queue = await this.getQueue();
      if (queue.length === 0) {
        return 0;
      }

      let syncedCount = 0;
      const failedItems = [];

      for (const item of queue) {
        try {
          // API'ye g√∂nder
          const success = await this.sendToServer(item);
          
          if (success) {
            // Ba≈üarƒ±lƒ± olanƒ± queue'dan kaldƒ±r
            await this.removeFromQueue(item.id);
            syncedCount++;
          } else {
            // Ba≈üarƒ±sƒ±z olanƒ± retry count'u artƒ±r
            item.retryCount++;
            if (item.retryCount >= item.maxRetries) {
              item.status = 'failed';
              failedItems.push(item);
            }
          }
        } catch (error) {
          console.error(`‚ùå Senkronizasyon hatasƒ± (${item.id}):`, error);
          item.retryCount++;
          if (item.retryCount >= item.maxRetries) {
            item.status = 'failed';
            failedItems.push(item);
          }
        }
      }

      // Failed item'larƒ± g√ºncelle
      if (failedItems.length > 0) {
        const updatedQueue = await this.getQueue();
        const newQueue = updatedQueue.map(item => {
          const failedItem = failedItems.find(fi => fi.id === item.id);
          return failedItem || item;
        });
        await this.saveQueue(newQueue);
      }

      // Son senkronizasyon zamanƒ±nƒ± kaydet
      await this.updateLastSync();

      console.log(`‚úÖ ${syncedCount} veri senkronize edildi`);
      return syncedCount;

    } catch (error) {
      console.error('‚ùå Genel senkronizasyon hatasƒ±:', error);
      throw error;
    }
  }

  // Veriyi sunucuya g√∂nder
  async sendToServer(item) {
    try {
      const endpoint = this.getEndpointForType(item.type);
      if (!endpoint) {
        console.warn(`‚ö†Ô∏è Bilinmeyen tip i√ßin endpoint yok: ${item.type}`);
        return false;
      }

      const response = await fetch(`${this.apiBaseUrl}${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...item.data,
          offlineQueueId: item.id,
          deviceId: item.deviceId,
          originalTimestamp: item.timestamp
        })
      });

      if (response.ok) {
        console.log(`‚úÖ Veri sunucuya g√∂nderildi: ${item.id}`);
        return true;
      } else {
        console.warn(`‚ö†Ô∏è Sunucu hatasƒ±: ${response.status} - ${item.id}`);
        return false;
      }

    } catch (error) {
      console.error(`‚ùå Sunucu g√∂nderim hatasƒ± (${item.id}):`, error);
      return false;
    }
  }

  // Tip i√ßin endpoint al
  getEndpointForType(type) {
    const endpoints = {
      'barcode_scan': '/api/scan',
      'awb-acceptance': '/api/awb/acceptance',
      'uld-loading': '/api/uld/loading',
      'dg-control': '/api/dg/control',
      'photo-capture': '/api/photo',
      'signature': '/api/signature'
    };

    return endpoints[type] || null;
  }

  // Son senkronizasyon zamanƒ±nƒ± g√ºncelle
  async updateLastSync() {
    try {
      await localforage.setItem(this.syncKey, new Date().toISOString());
    } catch (error) {
      console.error('‚ùå Son senkronizasyon zamanƒ± g√ºncellenemedi:', error);
    }
  }

  // Son senkronizasyon zamanƒ±nƒ± al
  async getLastSync() {
    try {
      return await localforage.getItem(this.syncKey);
    } catch (error) {
      console.error('‚ùå Son senkronizasyon zamanƒ± alƒ±namadƒ±:', error);
      return null;
    }
  }

  // Queue'yu temizle
  async clearQueue() {
    try {
      await localforage.removeItem(this.queueKey);
      console.log('üóëÔ∏è Queue temizlendi');
      return true;
    } catch (error) {
      console.error('‚ùå Queue temizleme hatasƒ±:', error);
      return false;
    }
  }

  // Queue istatistikleri
  async getQueueStats() {
    try {
      const queue = await this.getQueue();
      const stats = {
        total: queue.length,
        pending: queue.filter(item => item.status === 'pending').length,
        failed: queue.filter(item => item.status === 'failed').length,
        byType: {}
      };

      // Tip bazƒ±nda sayƒ±m
      queue.forEach(item => {
        if (!stats.byType[item.type]) {
          stats.byType[item.type] = 0;
        }
        stats.byType[item.type]++;
      });

      return stats;
    } catch (error) {
      console.error('‚ùå Queue istatistikleri alƒ±namadƒ±:', error);
      return null;
    }
  }


}
