import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import { FaBarcode, FaToggleOn, FaToggleOff, FaTrash, FaSync, FaInfoCircle, FaExclamationTriangle } from 'react-icons/fa';
import { counterSync } from '../services/CounterSyncService';
import { eventBus } from '../services/ToastManager';
import './BarcodeScanner.css';

const BarcodeScanner = ({ offlineQueue, onUpdate }) => {
  // üéØ TEK KAYNAKLI DURUM MODELƒ∞ - 4 Ana State
  const [offline, setOffline] = useState(false);  // Her zaman false - sadece online mod
  const [Q, setQ] = useState(0);                  // kuyruk: tamsayƒ±
  const [P, setP] = useState(0);                  // i≈üleniyor: tamsayƒ±
  const [T, setT] = useState(0);                  // toplam: tamsayƒ±

  // Appium durumu kontrol√º
  const [appiumConnected, setAppiumConnected] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);

  // Yardƒ±mcƒ± state (UI i√ßin)
  const [queueItems, setQueueItems] = useState([]);
  const [showClearModal, setShowClearModal] = useState(false);

  // Debounce koruma - 300ms
  const [lastActionTime, setLastActionTime] = useState(0);
  const DEBOUNCE_DELAY = 300;

  const testBarcodes = [
    { code: 'ULD-AKE12345AB', type: 'ULD', valid: true },
    { code: '123-45678901', type: 'AWB', valid: true },
    { code: 'ULD-ABC123', type: 'ULD', valid: false },
    { code: 'INVALID-CODE', type: 'INVALID', valid: false }
  ];

  // Appium saƒülƒ±k kontrol√º
  const checkAppiumHealth = async () => {
    try {
      // Timeout ve retry ile Appium kontrol√º
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 3000); // 3 saniye timeout

      const response = await fetch('http://10.0.2.2:4723/wd/hub/status', {
        signal: controller.signal,
        method: 'GET',
        headers: {
          'Accept': 'application/json',
        }
      });

      clearTimeout(timeoutId);
      const isConnected = response.ok;
      setAppiumConnected(isConnected);

      if (!isConnected) {
        eventBus.emit('appium:missing');
      }

      return isConnected;
    } catch (error) {
      console.log('Appium baƒülantƒ± hatasƒ±:', error.message);
      setAppiumConnected(false);
      eventBus.emit('appium:missing');
      return false;
    }
  };

  // Retry mekanizmasƒ± ile Appium kontrol√º
  const checkAppiumWithRetry = async (retries = 3) => {
    for (let i = 0; i < retries; i++) {
      const connected = await checkAppiumHealth();
      if (connected) {
        return true;
      }
      if (i < retries - 1) {
        console.log(`Appium retry ${i + 1}/${retries}...`);
        await new Promise(resolve => setTimeout(resolve, 1000)); // 1 saniye bekle
      }
    }
    return false;
  };

  // Debounce check fonksiyonu
  const checkDebounce = () => {
    const now = Date.now();
    if (now - lastActionTime < DEBOUNCE_DELAY) {
      console.log('‚ö†Ô∏è Debounced - √ßok hƒ±zlƒ± tƒ±klama');
      return false;
    }
    setLastActionTime(now);
    return true;
  };

  // Aksiyon logu helper
  const logAction = (action, before, after) => {
    console.log(`${action}: √∂nce Q=${before.Q},P=${before.P},T=${before.T}, offline=${before.offline} ‚Üí sonra Q=${after.Q},P=${after.P},T=${after.T}, offline=${after.offline}`);
  };

  // Buton durumlarƒ± hesaplama - BASƒ∞T MANTIK
  const getButtonStates = () => {
    const isAppiumConnected = appiumConnected;
    const isProcessingState = isProcessing || P > 0;
    const hasQueue = Number(Q) > 0; // String kontrol√º
    const isOffline = offline;

    // Debug deƒüerleri ekrana yazdƒ±r
    console.log('üîç DEBUG:', {
      Appium: isAppiumConnected ? '‚úì' : '√ó',
      Offline: isOffline ? '‚úì' : '√ó', 
      Kuyruk: Q,
      Processing: isProcessingState ? '‚úì' : '√ó'
    });

    return {
      scanButton: {
        disabled: !isAppiumConnected || isProcessingState,
        tooltip: !isAppiumConnected ? 'Appium baƒülƒ± deƒüil' : 
                isProcessingState ? 'ƒ∞≈ülem devam ediyor' : 'Barkod tara/oku'
      },
      processButton: {
        disabled: !isAppiumConnected || isProcessingState || !isOffline || !hasQueue,
        tooltip: !isAppiumConnected ? 'Appium baƒülƒ± deƒüil' :
                isProcessingState ? 'ƒ∞≈ülem devam ediyor' :
                !isOffline ? 'Online modda kuyruk i≈ülemesi yok' :
                !hasQueue ? 'Kuyruk bo≈ü' : 'Kuyruƒüu i≈üle'
      },
      clearButton: {
        disabled: !isAppiumConnected || isProcessingState || !isOffline || !hasQueue,
        tooltip: !isAppiumConnected ? 'Appium baƒülƒ± deƒüil' :
                isProcessingState ? 'ƒ∞≈ülem devam ediyor' :
                !isOffline ? 'Online modda kuyruk i≈ülemesi yok' :
                !hasQueue ? 'Kuyruk bo≈ü' : 'Kuyruƒüu temizle'
      }
    };
  };

  // Appium saƒülƒ±k kontrol√º ba≈ülatma
  useEffect(() => {
    // ƒ∞lk kontrol retry ile
    checkAppiumWithRetry();

    // Her 5 saniyede bir Appium durumunu kontrol et
    const interval = setInterval(() => {
      checkAppiumWithRetry();
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  // Kuyruƒüu temizle
  const handleClearQueue = () => {
    if (!checkDebounce()) return;

    // Offline mod kontrol√º
    if (!offline) {
      console.log('üö´ Kuyruƒüu Temizle blocked: online mode');
      return;
    }

    if (Q === 0) {
      eventBus.emit('queue:empty');
      return;
    }

    const before = { Q, P, T, offline };
    setQ(0);
    setQueueItems([]);
    const after = { Q: 0, P, T, offline };
    logAction('CLEAR_QUEUE', before, after);

    eventBus.emit('queue:cleared', { count: Q });
  };

  // Toggle fonksiyonu kaldƒ±rƒ±ldƒ± - sadece online mod

  // Mobil testini bitir
  const handleMobileTestComplete = async () => {
    if (!checkDebounce()) return;

    console.log(`üîÑ Mobil Testini Bitir clicked: T=${T}`);

    try {
      // URL'den runId'yi al
      const urlParams = new URLSearchParams(window.location.search);
      const runId = urlParams.get('runId');

      if (!runId) {
        toast.error('‚ùå RunId bulunamadƒ±');
        return;
      }

      // Backend'e mobil test tamamlandƒ± isteƒüi g√∂nder
      const response = await fetch('http://localhost:3001/api/mobile-test-completed', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          runId: runId,
          results: {
            passed: T, // Toplam ba≈üarƒ±lƒ± barkod sayƒ±sƒ±
            failed: 0, // ≈ûimdilik 0, ger√ßek uygulamada hesaplanabilir
            total: T
          }
        })
      });

      if (response.ok) {
        console.log('‚úÖ Mobil test tamamlandƒ± bildirildi');

        // 2 saniye sonra sayfayƒ± yenile (opsiyonel)
        setTimeout(() => {
          window.location.reload();
        }, 2000);
      } else {
        throw new Error('Backend hatasƒ±');
      }
    } catch (error) {
      console.error('‚ùå Mobil test tamamlandƒ± hatasƒ±:', error);
      toast.error('‚ùå Test tamamlandƒ± bildirilemedi');
    }
  };

  // üîÑ 2) Barkod Tara / Oku
  const simulateScan = async (barcode) => {
    if (!checkDebounce()) return;

    // Appium kontrol√º
    if (!appiumConnected) {
      eventBus.emit('appium:missing');
      return;
    }

    console.log(`üîÑ Barkod Tara clicked: offline=${offline}, Q=${Q}, P=${P}, T=${T}, barcode=${barcode?.code || 'manual'}`);

    const before = { Q, P, T, offline };

    // Offline modda kuyruƒüa ekle
    if (offline) {
      const newQ = Q + 1;
      setQ(newQ);
      setQueueItems(prev => [...prev, { id: Date.now(), code: barcode?.code || 'TEST-12345', timestamp: new Date() }]);

      const after = { Q: newQ, P, T, offline };
      logAction('Barkod Kuyruƒüa Eklendi', before, after);

      eventBus.emit('queue:add');
      return;
    }

    // Online modda direkt i≈üle
    // ƒ∞≈ülem kilidi
    setIsProcessing(true);
    setP(1);

    const afterStart = { Q, P: 1, T, offline };
    logAction('Barkod Tara (Online) - Ba≈üladƒ±', before, afterStart);

    try {
      // Test barkodu yoksa varsayƒ±lan olu≈ütur
      const testBarcode = barcode || { code: 'TEST-12345', valid: true };

      // Sim√ºlasyon - i≈üleme s√ºresi (1000ms)
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Sonuca g√∂re:
      if (testBarcode.valid) {
        // Ba≈üarƒ±lƒ±: T = T + 1
        const newT = T + 1;
        setT(newT);
        add24hTotal(1);

        const afterSuccess = { Q, P: 0, T: newT, offline };
        setP(0); // P = 0 (i≈ülem bitti)
        setIsProcessing(false); // ƒ∞≈ülem kilidi kaldƒ±r
        logAction('Barkod Tara (Online) - Ba≈üarƒ±lƒ±', afterStart, afterSuccess);

        // 150-300ms g√∂r√ºn√ºr tutma i√ßin toast'ƒ± geciktir
        setTimeout(() => {
          toast.success(`‚úÖ Barkod i≈ülendi (T=${newT})`);
          eventBus.emit('barcode:ok', { code: testBarcode.code });
        }, 200);

      } else {
        // Ba≈üarƒ±sƒ±z: T deƒüi≈ümez
        setP(0); // P = 0 (i≈ülem bitti)
        setIsProcessing(false); // ƒ∞≈ülem kilidi kaldƒ±r

        const afterFailed = { Q, P: 0, T, offline };
        logAction('Barkod Tara (Online) - Ba≈üarƒ±sƒ±z', afterStart, afterFailed);

        setTimeout(() => {
          toast.error('‚ùå ƒ∞≈ülem ba≈üarƒ±sƒ±z');
          eventBus.emit('barcode:fail');
        }, 200);
      }

    } catch (error) {
      // Hata durumu: P = 0, T deƒüi≈ümez
      setP(0);
      setIsProcessing(false); // ƒ∞≈ülem kilidi kaldƒ±r
      toast.error('‚ùå ƒ∞≈ülem hatasƒ±');
    }
  };

  // üîÑ 3) Kuyruƒüu ƒ∞≈üle (yalnƒ±z offline ve Q>0 iken)
  const processQueue = async () => {
    if (!checkDebounce()) return;

    // Appium kontrol√º
    if (!appiumConnected) {
      eventBus.emit('appium:missing');
      return;
    }

    // Offline mod kontrol√º
    if (!offline) {
      console.log('üö´ Kuyruƒüu ƒ∞≈üle blocked: online mode');
      return;
    }

    console.log(`üîÑ Kuyruƒüu ƒ∞≈üle clicked: offline=${offline}, Q=${Q}, P=${P}, T=${T}`);

    if (Q === 0) {
      eventBus.emit('queue:empty');
      return;
    }

    const before = { Q, P, T, offline };
    const batchSize = 5;
    let currentQ = Q;
    let currentT = T;

    // ƒ∞≈ülem kilidi
    setIsProcessing(true);
    setP(1);

    try {
      // Batch'ler halinde i≈üle
      while (currentQ > 0) {
        // Parti ba≈üƒ±: P = min(Q, batchSize)
        const currentBatchSize = Math.min(currentQ, batchSize);
        setP(currentBatchSize);

        console.log(`‚ö° Batch ba≈üladƒ±: P=${currentBatchSize}, Q=${currentQ}`);

        // Sim√ºlasyon - batch i≈üleme s√ºresi
        await new Promise(resolve => setTimeout(resolve, 2000));

        // Her √∂ƒüe tamamlandƒ±k√ßa (sim√ºlasyon %85 ba≈üarƒ±)
        const successCount = Math.floor(currentBatchSize * 0.85) || Math.min(1, currentBatchSize);

        for (let i = 0; i < currentBatchSize; i++) {
          if (i < successCount) {
            // Ba≈üarƒ±lƒ±: T = T + 1, Q = Q - 1, P = P - 1
            currentT++;
            setT(currentT);
            add24hTotal(1);
          }
          // Her durumda: Q = Q - 1, P = P - 1
          currentQ--;
          setQ(currentQ);
          setP(Math.max(0, currentBatchSize - i - 1));

          // Kƒ±sa gecikme (UI'da deƒüi≈üimi g√∂rmek i√ßin)
          await new Promise(resolve => setTimeout(resolve, 100));
        }

        console.log(`‚úÖ Batch tamamlandƒ±: Q=${currentQ}, T=${currentT}, P=0`);

        // Parti bitti: Q > 0 ise yeni parti; deƒüilse P = 0 ve dur
        if (currentQ > 0) {
          console.log(`üîÑ Yeni parti ba≈ülƒ±yor: Q=${currentQ}`);
        }
      }

      // Final durumu
      setP(0);
      setIsProcessing(false); // ƒ∞≈ülem kilidi kaldƒ±r
      const after = { Q: currentQ, P: 0, T: currentT, offline };
      logAction('Kuyruƒüu ƒ∞≈üle - Tamamlandƒ±', before, after);

      // Kuyruƒüu temizle
      if (offlineQueue) {
        await offlineQueue.clearQueue();
        await updateQueueItems();
      }

      toast.success(`üéâ Kuyruk i≈ülendi: ${currentT - before.T} ba≈üarƒ±lƒ±`);
      eventBus.emit('queue:result', { 
        ok: currentT - before.T, 
        fail: 0 
      });
    } catch (error) {
      setP(0);
      setIsProcessing(false); // ƒ∞≈ülem kilidi kaldƒ±r
      toast.error('‚ùå Kuyruk i≈üleme hatasƒ±');
      console.error('Kuyruk i≈üleme hatasƒ±:', error);
    }
  };

  // üîÑ 4) Kuyruƒüu Temizle (onaydan sonra)
  const clearQueue = async () => {
    if (!checkDebounce()) return;

    console.log(`üîÑ Kuyruƒüu Temizle clicked: offline=${offline}, Q=${Q}, P=${P}, T=${T}`);

    if (Q === 0) {
      console.log('üö´ Kuyruƒüu Temizle blocked: Q=0');
      toast.info('‚ÑπÔ∏è Kuyruk zaten bo≈ü');
      return;
    }

    const before = { Q, P, T, offline };

    if (P === 0) {
      // P = 0 ise: Q = 0
      setQ(0);

  // Kuyruƒüu temizle
      if (offlineQueue) {
        await offlineQueue.clearQueue();
        await updateQueueItems();
      }

      const after = { Q: 0, P, T, offline }; // T hi√ßbir ko≈üulda deƒüi≈ümez
      logAction('Kuyruƒüu Temizle', before, after);

      toast.success(`üóëÔ∏è Kuyruk temizlendi (${before.Q} kayƒ±t)`);

    } else {
      // P > 0 ise: √∂nce i≈ülemi iptal et ‚Üí P = 0, sonra Q = 0
      setP(0); // ƒ∞≈ülemi iptal et
      setQ(0); // Kuyruƒüu temizle

      if (offlineQueue) {
      await offlineQueue.clearQueue();
        await updateQueueItems();
      }

      const after = { Q: 0, P: 0, T, offline }; // T hi√ßbir ko≈üulda deƒüi≈ümez
      logAction('Kuyruƒüu Temizle (ƒ∞≈ülem ƒ∞ptal)', before, after);

      toast.success(`üõë ƒ∞≈ülem durduruldu ve kuyruk temizlendi (${before.Q} kayƒ±t)`);
    }
  };

  const updateQueueItems = async () => {
    if (offlineQueue) {
      const items = await offlineQueue.getQueue();
      setQueueItems(items);
      if (onUpdate) onUpdate();
    }
  };

  // Initialization - saya√ßlarƒ± senkronize et
  useEffect(() => {
    const initializeCounters = async () => {
      if (offlineQueue) {
        // Q: Kuyruk sayƒ±sƒ±
        const queue = await offlineQueue.getQueue();
        setQ(queue.length);
        setQueueItems(queue);

        // T: 24 saat toplam (localStorage'dan al)
        const stored24h = JSON.parse(localStorage.getItem('24h_records') || '[]');
        setT(stored24h.length);

        // P: Ba≈ülangƒ±√ßta 0
        setP(0);

        console.log(`üîÑ Initialized: Q=${queue.length}, P=0, T=${stored24h.length}, offline=${offline}`);
      }
    };

    initializeCounters();
  }, [offlineQueue]);

  // 24 saatlik toplama ekleme (T artƒ±rmak i√ßin)
  const add24hTotal = (count = 1) => {
    // Timestamp'li kayƒ±t tut (rolling window i√ßin)
    const now = new Date().toISOString();
    const records = JSON.parse(localStorage.getItem('24h_records') || '[]');
    for (let i = 0; i < count; i++) {
      records.push({ timestamp: now });
    }

    // 24 saatten eski kayƒ±tlarƒ± temizle
    const oneDayAgo = new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString();
    const filteredRecords = records.filter(record => record.timestamp > oneDayAgo);

    localStorage.setItem('24h_records', JSON.stringify(filteredRecords));
  };

  // Render logu - her render'da
  console.log(`üîÑ Render'da: render with offline=${offline}, Q=${Q}, P=${P}, T=${T}`);

  return (
    <div className="barcode-scanner">
      {/* Header */}
      <div className="scanner-header">
        <h1 className="scanner-title">Dijital ƒ∞kiz Tabanlƒ± Test Sistemi</h1>
        <div className={`status-badge ${offline ? 'offline' : 'online'}`}>
          {offline ? 'üì± Offline' : 'üåê Online'}
        </div>
      </div>

      {/* Q/P/T Saya√ß Kutucuklarƒ± - TEK KAYNAKLI STATE */}
      <div className="stats-section">
        <div className="stats-grid">
          <div className="stat-card queue">
            <div className="stat-value">{Q}</div>
            <div className="stat-label">Kuyruk</div>
          </div>
          <div className="stat-card processing">
            <div className="stat-value">{P}</div>
            <div className="stat-label">ƒ∞≈üleniyor</div>
          </div>
          <div className="stat-card total">
            <div className="stat-value">{T}</div>
            <div className="stat-label">Toplam</div>
            <div className="stat-subtitle">24 saat</div>
          </div>
        </div>
      </div>

      {/* DEBUG Bƒ∞LGƒ∞LERƒ∞ */}
      <div style={{
        background: '#f0f0f0',
        padding: '10px',
        margin: '10px 0',
        borderRadius: '5px',
        fontSize: '12px',
        textAlign: 'center'
      }}>
        <strong>DEBUG:</strong> Appium: {appiumConnected ? '‚úì' : '√ó'} | 
        Offline: {offline ? '‚úì' : '√ó'} | 
        Kuyruk: {Q} | 
        Processing: {(isProcessing || P > 0) ? '‚úì' : '√ó'}
      </div>

      {/* Offline Toggle */}
      <div className="toggle-section">
        <div className="toggle-container">
          <span className="toggle-label">Offline Modu</span>
          <button 
            className={`toggle-btn ${offline ? 'active' : ''}`}
            onClick={() => setOffline(!offline)}
          >
            {offline ? <FaToggleOn /> : <FaToggleOff />}
          </button>
        </div>
      </div>

      {/* Action Buttons - BUTON ETKƒ∞NLƒ∞K KURALLARI */}
      <div className="action-section">
        {(() => {
          const buttonStates = getButtonStates();
          return (
            <>
              {/* Barkod Tara/Oku */}
              <button 
                className="action-btn primary"
                onClick={simulateScan}
                disabled={buttonStates.scanButton.disabled}
                title={buttonStates.scanButton.tooltip}
              >
                <FaBarcode />
                Barkod Tara/Oku
              </button>

              {/* Kuyruƒüu ƒ∞≈üle */}
              <button 
                className="action-btn secondary"
                onClick={processQueue}
                disabled={buttonStates.processButton.disabled}
                title={buttonStates.processButton.tooltip}
              >
                <FaSync className={P > 0 ? 'spinning' : ''} />
                {P > 0 ? 'ƒ∞≈üleniyor...' : 'Kuyruƒüu ƒ∞≈üle'} ({Q})
              </button>

              {/* Kuyruƒüu Temizle */}
              <button 
                className="action-btn danger"
                onClick={handleClearQueue}
                disabled={buttonStates.clearButton.disabled}
                title={buttonStates.clearButton.tooltip}
              >
                <FaTrash />
                Kuyruƒüu Temizle
              </button>
            </>
          );
        })()}

      </div>

      {/* Test Barkodlarƒ± */}
      <div className="test-section">
        <h2 className="section-title">Test Barkodlarƒ±</h2>
        <div className="test-grid">
          {testBarcodes.map((barcode, index) => (
            <div 
              key={index}
              className={`test-card ${barcode.valid ? 'valid' : 'invalid'}`}
              onClick={() => simulateScan(barcode)}
              onTouchStart={(e) => {
                // iOS i√ßin touch feedback
                e.currentTarget.style.transform = 'scale(0.95)';
              }}
              onTouchEnd={(e) => {
                // Touch end'de normal haline d√∂n
                setTimeout(() => {
                  e.currentTarget.style.transform = '';
                }, 150);
              }}
              style={{
                cursor: 'pointer',
                WebkitTapHighlightColor: 'transparent'
              }}
            >
              <div className="barcode-icon">
                <FaBarcode />
              </div>
              <div className="barcode-info">
              <div className="barcode-code">{barcode.code}</div>
              <div className="barcode-type">{barcode.type}</div>
                <div className={`barcode-status ${barcode.valid ? 'valid' : 'invalid'}`}>
                  {barcode.valid ? '‚úÖ Ge√ßerli' : '‚ùå Ge√ßersiz'}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Queue Items */}
      <div className="queue-section">
        <h3 className="queue-title">
          Kuyruk ({queueItems.length})
        </h3>

        {queueItems.length === 0 ? (
          <div className="empty-queue">
            <FaInfoCircle />
            <span>Kuyruk bo≈ü</span>
          </div>
        ) : (
          <div className="queue-list">
            {queueItems.map((item, index) => (
              <div key={item.id} className="queue-item">
                <div className="item-info">
                  <span className="item-barcode">{item.data?.barcode || 'N/A'}</span>
                  <span className="item-type">{item.data?.type || 'scan'}</span>
                  <span className={`item-status ${item.data?.status || 'pending'}`}>
                    {item.data?.status === 'pending' && '‚è≥ Bekliyor'}
                    {item.data?.status === 'processing' && 'üîÑ ƒ∞≈üleniyor'}
                    {item.data?.status === 'success' && '‚úÖ Ba≈üarƒ±lƒ±'}
