import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import { FaBarcode, FaToggleOn, FaToggleOff, FaTrash, FaSync, FaInfoCircle, FaExclamationTriangle } from 'react-icons/fa';
import { counterSync } from '../services/CounterSyncService';
import './BarcodeScanner.css';

const SyncedBarcodeScanner = ({ offlineQueue, onUpdate }) => {
  // üéØ TEK KAYNAKLI DURUM MODELƒ∞ - 4 Ana State (API Senkronize)
  const [offline, setOffline] = useState(false);  // true/false
  const [Q, setQ] = useState(0);                  // kuyruk: tamsayƒ±
  const [P, setP] = useState(0);                  // i≈üleniyor: tamsayƒ±
  const [T, setT] = useState(0);                  // toplam: tamsayƒ±
  
  // Yardƒ±mcƒ± state (UI i√ßin)
  const [queueItems, setQueueItems] = useState([]);
  const [showClearModal, setShowClearModal] = useState(false);
  
  // Debounce koruma - 300ms
  const [lastActionTime, setLastActionTime] = useState(0);
  const DEBOUNCE_DELAY = 300;

  const testBarcodes = [
    { code: 'ULD-AKE12345AB', type: 'ULD', valid: true },
    { code: '123-45678901', type: 'AWB', valid: true },
    { code: 'ULD-ABC123', type: 'ULD', valid: false },
    { code: 'INVALID-CODE', type: 'INVALID', valid: false }
  ];

  // Debounce check fonksiyonu
  const checkDebounce = () => {
    const now = Date.now();
    if (now - lastActionTime < DEBOUNCE_DELAY) {
      console.log('‚ö†Ô∏è Debounced - √ßok hƒ±zlƒ± tƒ±klama');
      return false;
    }
    setLastActionTime(now);
    return true;
  };

  // Aksiyon logu helper
  const logAction = (action, before, after) => {
    console.log(`${action}: √∂nce Q=${before.Q},P=${before.P},T=${before.T}, offline=${before.offline} ‚Üí sonra Q=${after.Q},P=${after.P},T=${after.T}, offline=${after.offline}`);
  };

  // üîÑ 1) Toggle: Offline Modu - TEK KAYNAK KONTROLL√ú
  const handleOfflineModeToggle = async (newValue) => {
    if (!checkDebounce()) {
      console.log('‚ö†Ô∏è Toggle debounced - √ßok hƒ±zlƒ± tƒ±klama');
      return;
    }

    console.log(`üîÑ onChange: set offline = ${newValue} (√∂nceki: ${offline})`);
    
    const before = { Q, P, T, offline };
    
    // TEK KAYNAK: sadece burada state g√ºncelle
    setOffline(newValue);
    await counterSync.updateCounter('offline', newValue);
    
    const after = { Q, P, T, offline: newValue };
    logAction('Toggle Offline', before, after);
    
    // Doƒüru e≈üleme: offline=true ‚Üí ye≈üil ‚Üí "Offline moda ge√ßildi"
    setTimeout(() => {
      console.log(`üîÑ Toggle completed: offline=${newValue}`);
      toast.info(newValue ? 'üì± Offline moda ge√ßildi' : 'üåê Online moda ge√ßildi');
    }, 50);
  };

  // üîÑ 2) Barkod Tara / Oku - API Senkronize
  const simulateScan = async (barcode) => {
    if (!checkDebounce()) return;
    
    console.log(`üîÑ Barkod Tara clicked: offline=${offline}, Q=${Q}, P=${P}, T=${T}, barcode=${barcode.code}`);
    
    const before = { Q, P, T, offline };
    
    if (offline) {
      // offline = true (ye≈üil): Q = Q + 1, P deƒüi≈ümez (0'da kalƒ±r), T deƒüi≈ümez
      const newQ = Q + 1;
      setQ(newQ);
      await counterSync.updateCounter('Q', newQ);
      
      // Kuyruƒüa ekle
      if (offlineQueue) {
        const scanData = {
          type: 'barcode_scan',
          barcode: barcode.code,
          barcode_type: barcode.type,
          timestamp: Date.now(),
          valid: barcode.valid
        };
        await offlineQueue.addToQueue(scanData);
        await updateQueueItems();
      }
      
      const after = { Q: newQ, P, T, offline };
      logAction('Barkod Tara (Offline)', before, after);
      
      toast.success(`üì• √áevrimdƒ±≈üƒ±: kuyruƒüa eklendi (Q=${newQ})`);
      
    } else {
      // offline = false (gri): P = 1 (i≈ülem ba≈üladƒ±)
      setP(1);
      await counterSync.updateCounter('P', 1);
      
      const afterStart = { Q, P: 1, T, offline };
      logAction('Barkod Tara (Online) - Ba≈üladƒ±', before, afterStart);
      
      try {
        // Sim√ºlasyon - i≈üleme s√ºresi (1000ms)
        await new Promise(resolve => setTimeout(resolve, 1000));
        
        // Sonuca g√∂re:
        if (barcode.valid) {
          const newT = T + 1;
          setT(newT);
          await counterSync.updateCounter('T', newT);
          add24hTotal(1);
          
          const afterSuccess = { Q, P: 0, T: newT, offline };
          setP(0); // P = 0 (i≈ülem bitti)
          await counterSync.updateCounter('P', 0);
          logAction('Barkod Tara (Online) - Ba≈üarƒ±lƒ±', afterStart, afterSuccess);
          
          setTimeout(() => {
            toast.success(`‚úÖ Barkod i≈ülendi (T=${newT})`);
          }, 200);
          
        } else {
          // Ba≈üarƒ±sƒ±z: T deƒüi≈ümez
          setP(0); // P = 0 (i≈ülem bitti)
          await counterSync.updateCounter('P', 0);
          
          const afterFailed = { Q, P: 0, T, offline };
          logAction('Barkod Tara (Online) - Ba≈üarƒ±sƒ±z', afterStart, afterFailed);
          
          setTimeout(() => {
            toast.error('‚ùå ƒ∞≈ülem ba≈üarƒ±sƒ±z');
          }, 200);
        }
        
      } catch (error) {
        // Hata durumu: P = 0, T deƒüi≈ümez
        setP(0);
        await counterSync.updateCounter('P', 0);
        toast.error('‚ùå ƒ∞≈ülem hatasƒ±');
      }
    }
  };

  // üîÑ 3) Kuyruƒüu ƒ∞≈üle (yalnƒ±z online ve Q>0 iken) - API Senkronize
  const processQueue = async () => {
    if (!checkDebounce()) return;
    
    console.log(`üîÑ Kuyruƒüu ƒ∞≈üle clicked: offline=${offline}, Q=${Q}, P=${P}, T=${T}`);
    
    if (offline) {
      console.log('üö´ Kuyruƒüu ƒ∞≈üle blocked: offline mode');
      toast.warning('‚ö†Ô∏è Offline modda kuyruk i≈ülenez');
      return;
    }

    if (Q === 0) {
      toast.info('‚ÑπÔ∏è Kuyruk bo≈ü');
      return;
    }

    const before = { Q, P, T, offline };
    const batchSize = 5;
    let currentQ = Q;
    let currentT = T;
    
    try {
      // Batch'ler halinde i≈üle
      while (currentQ > 0) {
        const currentBatchSize = Math.min(currentQ, batchSize);
        setP(currentBatchSize);
        await counterSync.updateCounter('P', currentBatchSize);
        
        console.log(`‚ö° Batch ba≈üladƒ±: P=${currentBatchSize}, Q=${currentQ}`);
        
        await new Promise(resolve => setTimeout(resolve, 2000));
        
        const successCount = Math.floor(currentBatchSize * 0.85) || Math.min(1, currentBatchSize);
        
        for (let i = 0; i < currentBatchSize; i++) {
          if (i < successCount) {
            currentT++;
            setT(currentT);
            await counterSync.updateCounter('T', currentT);
            add24hTotal(1);
          }
          currentQ--;
          setQ(currentQ);
          await counterSync.updateCounter('Q', currentQ);
          setP(Math.max(0, currentBatchSize - i - 1));
          await counterSync.updateCounter('P', Math.max(0, currentBatchSize - i - 1));
          
          await new Promise(resolve => setTimeout(resolve, 100));
        }
        
        console.log(`‚úÖ Batch tamamlandƒ±: Q=${currentQ}, T=${currentT}, P=0`);
      }
      
      setP(0);
      await counterSync.updateCounter('P', 0);
      const after = { Q: currentQ, P: 0, T: currentT, offline };
      logAction('Kuyruƒüu ƒ∞≈üle - Tamamlandƒ±', before, after);
      
      if (offlineQueue) {
        await offlineQueue.clearQueue();
        await updateQueueItems();
      }
      
      toast.success(`üéâ Kuyruk i≈ülendi: ${currentT - before.T} ba≈üarƒ±lƒ±`);
      
    } catch (error) {
      setP(0);
      await counterSync.updateCounter('P', 0);
      toast.error('‚ùå Kuyruk i≈üleme hatasƒ±');
    }
  };

  // üîÑ 4) Kuyruƒüu Temizle (onaydan sonra) - API Senkronize
  const clearQueue = async () => {
    if (!checkDebounce()) return;
    
    console.log(`üîÑ Kuyruƒüu Temizle clicked: offline=${offline}, Q=${Q}, P=${P}, T=${T}`);
    
    if (Q === 0) {
      console.log('üö´ Kuyruƒüu Temizle blocked: Q=0');
      toast.info('‚ÑπÔ∏è Kuyruk zaten bo≈ü');
      return;
    }
    
    const before = { Q, P, T, offline };
    
    if (P === 0) {
      setQ(0);
      await counterSync.updateCounter('Q', 0);

      if (offlineQueue) {
        await offlineQueue.clearQueue();
        await updateQueueItems();
      }
      
      const after = { Q: 0, P, T, offline };
      logAction('Kuyruƒüu Temizle', before, after);
      
      toast.success(`üóëÔ∏è Kuyruk temizlendi (${before.Q} kayƒ±t)`);
      
    } else {
      setP(0);
      setQ(0);
      await counterSync.updateCounters({ P: 0, Q: 0 });
      
      if (offlineQueue) {
        await offlineQueue.clearQueue();
        await updateQueueItems();
      }
      
      const after = { Q: 0, P: 0, T, offline };
      logAction('Kuyruƒüu Temizle (ƒ∞≈ülem ƒ∞ptal)', before, after);
      
      toast.success(`üõë ƒ∞≈ülem durduruldu ve kuyruk temizlendi (${before.Q} kayƒ±t)`);
    }
  };

  const updateQueueItems = async () => {
    if (offlineQueue) {
      const items = await offlineQueue.getQueue();
      setQueueItems(items);
      if (onUpdate) onUpdate();
    }
  };

  // Initialization - saya√ßlarƒ± API'den senkronize et
  useEffect(() => {
    const initializeCounters = async () => {
      // 1. API'den global saya√ßlarƒ± √ßek
      const apiCounters = await counterSync.getCounters();
      if (apiCounters) {
        setQ(apiCounters.Q);
        setP(apiCounters.P);
        setT(apiCounters.T);
        setOffline(apiCounters.offline);
        console.log(`üîÑ API'den y√ºklendi:`, apiCounters);
      }
      
      // 2. Lokal kuyruk verilerini y√ºkle
      if (offlineQueue) {
        const queue = await offlineQueue.getQueue();
        setQueueItems(queue);
      }
    };
    
    initializeCounters();
    
    // Otomatik senkronizasyon ba≈ülat
    counterSync.startAutoSync(2000);
    
    // API deƒüi≈üikliklerini dinle
    const unsubscribe = counterSync.addListener((newCounters) => {
      console.log('üîÑ API g√ºncellemesi:', newCounters);
      setQ(newCounters.Q);
      setP(newCounters.P);
      setT(newCounters.T);
      setOffline(newCounters.offline);
    });
    
    return () => {
      counterSync.stopAutoSync();
      unsubscribe();
    };
  }, [offlineQueue]);

  // 24 saatlik toplama ekleme (T artƒ±rmak i√ßin)
  const add24hTotal = (count = 1) => {
    const now = new Date().toISOString();
    const records = JSON.parse(localStorage.getItem('24h_records') || '[]');
    for (let i = 0; i < count; i++) {
      records.push({ timestamp: now });
    }
    
    const oneDayAgo = new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString();
    const filteredRecords = records.filter(record => record.timestamp > oneDayAgo);
    
    localStorage.setItem('24h_records', JSON.stringify(filteredRecords));
  };

  // üîÑ RENDER LOG - State takibi
  console.log(`üîÑ render: offline=${offline}, Q=${Q}, P=${P}, T=${T}`);

  return (
    <div className="barcode-scanner">
      {/* Header */}
      <div className="scanner-header">
        <h1 className="scanner-title">üîÑ Senkronize Barkod Sistemi</h1>
        <div className={`status-badge ${offline ? 'offline' : 'online'}`}>
          {offline ? 'üì± Offline' : 'üåê Online'} ‚Ä¢ Senkronize
        </div>
      </div>

      {/* Q/P/T Saya√ß Kutucuklarƒ± - API SENKRONIZE */}
      <div className="stats-section">
        <div className="stats-grid">
          <div className="stat-card queue">
            <div className="stat-value">{Q}</div>
            <div className="stat-label">Kuyruk</div>
          </div>
          <div className="stat-card processing">
            <div className="stat-value">{P}</div>
            <div className="stat-label">ƒ∞≈üleniyor</div>
          </div>
          <div className="stat-card total">
            <div className="stat-value">{T}</div>
            <div className="stat-label">Toplam</div>
            <div className="stat-subtitle">24 saat</div>
          </div>
        </div>
      </div>

      {/* Offline Toggle */}
      <div className="toggle-section">
        <div className="toggle-wrapper">
          <span className="toggle-label">Offline Modu</span>
          <div 
            className="toggle-hit-area"
            onClick={() => handleOfflineModeToggle(!offline)}
            onTouchStart={(e) => {
              e.preventDefault();
            }}
            onTouchEnd={(e) => {
              e.preventDefault();
            }}
            style={{
              minWidth: '44px',
              minHeight: '44px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              touchAction: 'manipulation',
              cursor: 'pointer',
              WebkitTapHighlightColor: 'transparent',
              zIndex: 10 // Toast √ºst√ºne √ßƒ±k
            }}
          >
            <button 
              className={`toggle-switch ${offline ? 'active' : ''}`}
              style={{ pointerEvents: 'none' }}
              data-offline={offline}
              data-class={offline ? 'active' : 'inactive'}
            >
              <div className="toggle-handle"></div>
            </button>
          </div>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="action-section">
        <button 
          className="action-btn primary"
          onClick={processQueue}
          onTouchStart={(e) => {
            if (!e.currentTarget.disabled) {
              e.currentTarget.style.transform = 'scale(0.95)';
            }
          }}
          onTouchEnd={(e) => {
            setTimeout(() => {
              e.currentTarget.style.transform = '';
            }, 150);
          }}
          disabled={offline || Q === 0 || P > 0}
          title={
            offline 
              ? 'Offline modda pasif - Online moda ge√ßin' 
              : Q === 0 
                ? 'Kuyruk bo≈ü - √∂nce barkod ekleyin'
                : P > 0
                  ? 'ƒ∞≈ülem devam ediyor'
                  : 'Kuyruktaki barkodlarƒ± toplu i≈üle'
          }
        >
          <FaSync className={P > 0 ? 'spinning' : ''} />
          {P > 0 ? 'ƒ∞≈üleniyor...' : 'Kuyruƒüu ƒ∞≈üle'}
        </button>

        <button 
          className="action-btn danger"
          onClick={() => {
            if (Q === 0) {
              toast.info('‚ÑπÔ∏è Kuyruk zaten bo≈ü');
              return;
            }
            setShowClearModal(true);
          }}
          onTouchStart={(e) => {
            if (!e.currentTarget.disabled) {
              e.currentTarget.style.transform = 'scale(0.95)';
            }
          }}
          onTouchEnd={(e) => {
            setTimeout(() => {
              e.currentTarget.style.transform = '';
            }, 150);
          }}
          disabled={Q === 0}
          title={
            Q === 0 
              ? 'Kuyruk bo≈ü - temizlenecek barkod yok'
              : `${Q} barkod var - kuyruƒüu temizle`
          }
        >
          <FaTrash />
          Temizle
        </button>
      </div>

      {/* Test Barkodlarƒ± */}
      <div className="test-section">
        <h2 className="section-title">Test Barkodlarƒ±</h2>
        <div className="test-grid">
          {testBarcodes.map((barcode, index) => (
            <div 
              key={index}
              className={`test-card ${barcode.valid ? 'valid' : 'invalid'}`}
              onClick={() => simulateScan(barcode)}
              onTouchStart={(e) => {
                e.currentTarget.style.transform = 'scale(0.95)';
              }}
              onTouchEnd={(e) => {
                setTimeout(() => {
                  e.currentTarget.style.transform = '';
                }, 150);
              }}
              style={{
                cursor: 'pointer',
                WebkitTapHighlightColor: 'transparent'
              }}
            >
              <div className="barcode-icon">
                <FaBarcode />
              </div>
              <div className="barcode-info">
                <div className="barcode-code">{barcode.code}</div>
                <div className="barcode-type">{barcode.type}</div>
                <div className={`barcode-status ${barcode.valid ? 'valid' : 'invalid'}`}>
                  {barcode.valid ? '‚úÖ Ge√ßerli' : '‚ùå Ge√ßersiz'}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Queue Items */}
      <div className="queue-section">
        <h3 className="queue-title">
          Kuyruk ({queueItems.length})
        </h3>
        
        {queueItems.length === 0 ? (
          <div className="empty-queue">
            <FaInfoCircle />
            <span>Kuyruk bo≈ü</span>
          </div>
        ) : (
          <div className="queue-list">
            {queueItems.map((item, index) => (
              <div key={item.id} className="queue-item">
                <div className="item-info">
                  <span className="item-barcode">{item.data?.barcode || 'N/A'}</span>
                  <span className="item-type">{item.data?.type || 'scan'}</span>
                  <span className={`item-status ${item.data?.status || 'pending'}`}>
                    {item.data?.status === 'pending' && '‚è≥ Bekliyor'}
                    {item.data?.status === 'processing' && 'üîÑ ƒ∞≈üleniyor'}
                    {item.data?.status === 'success' && '‚úÖ Ba≈üarƒ±lƒ±'}
                    {item.data?.status === 'error' && '‚ùå Hatalƒ±'}
                  </span>
                </div>
              </div>
            ))}
          </div>
        )}

        {Q >= 100 && (
          <div className="long-queue-warning">
            <FaInfoCircle />
            <span>Kuyruk b√ºy√ºk, arka planda g√∂nderiliyor...</span>
          </div>
        )}
      </div>

      {/* Temizleme Onay Modalƒ± */}
      {showClearModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h4>Kuyruƒüu temizle</h4>
            <p>Kuyrukta <strong>{Q}</strong> kayƒ±t var. Bu i≈ülem geri alƒ±namaz.</p>
            {P > 0 && (
              <div className="warning-text">
                ‚ö†Ô∏è ≈ûu anda {P} barkod i≈üleniyor. ƒ∞≈ülemi durdurup kuyruƒüu temizlemek istiyor musunuz?
              </div>
            )}
            <div className="modal-buttons">
              <button 
                className="btn btn-secondary"
                onClick={() => setShowClearModal(false)}
              >
                Vazge√ß
              </button>
              <button 
                className="btn btn-danger"
                onClick={() => {
                  clearQueue();
                  setShowClearModal(false);
                }}
              >
                {P > 0 ? 'Evet, durdur ve sil' : 'Evet, temizle'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SyncedBarcodeScanner;
