import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './App.css';
import { eventBus } from './services/ToastManager';

// Offline Queue Service
import { OfflineQueueService } from './services/OfflineQueueService';

// Components
import BarcodeScanner from './components/BarcodeScanner';
import SyncedBarcodeScanner from './components/SyncedBarcodeScanner';
import AwbAcceptance from './components/AwbAcceptance';
import UldLoading from './components/UldLoading';
import MobileNavigation from './components/MobileNavigation';

// Icons
import { 
  FaBarcode, 
  FaClipboardCheck, 
  FaBoxes, 
  FaWifi,
  FaTimes,
  FaSync,
  FaExclamationTriangle
} from 'react-icons/fa';

// Status Chip Component
const StatusChip = ({ status, isOnline, isSyncing }) => {
  let statusText = '';
  let statusClass = '';
  let statusIcon = null;

  if (isSyncing) {
    statusText = 'Senkronize ediliyor...';
    statusClass = 'syncing';
    statusIcon = <FaSync className="spinning" />;
  } else if (isOnline) {
    statusText = 'Baƒülƒ±';
    statusClass = 'online';
    statusIcon = <FaWifi />;
  } else {
    statusText = 'Offline';
    statusClass = 'offline';
    statusIcon = <FaTimes />;
  }

  return (
    <div className={`status-chip ${statusClass}`}>
      {statusIcon}
      <span>{statusText}</span>
    </div>
  );
};

// MobileNavigation zaten import edilmi≈ü

const App = () => {
  const [isOnline, setIsOnline] = useState(navigator.onLine);
  const [queueCount, setQueueCount] = useState(0);
  const [isSyncing, setIsSyncing] = useState(false);
  const [currentUser, setCurrentUser] = useState({
    id: 'user-001',
    name: 'Saha Operat√∂r√º',
    role: 'field-operator',
    deviceId: `device-${Date.now()}`
  });

  // Offline Queue Service
  const offlineQueue = new OfflineQueueService();

  // Online/Offline durumu kontrol et
  useEffect(() => {
    const handleOnline = () => {
      setIsOnline(true);
      toast.success('‚úÖ √áevrimi√ßi baƒülantƒ± kuruldu');
      syncOfflineQueue();
    };

    const handleOffline = () => {
      setIsOnline(false);
      toast.info('‚ÑπÔ∏è Offline modda tarama kuyruƒüa eklendi.');
    };

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    // Queue sayƒ±sƒ±nƒ± g√ºncelle
    updateQueueCount();

    // Mobil test hazƒ±r olduƒüunda toast g√∂ster
    eventBus.emit('mobile:ready');

    // RunId ile backend'e baƒülan
    connectToBackend();

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  // Backend'e runId ile baƒülan
  const connectToBackend = async () => {
    try {
      // URL'den runId'yi al (query parameter olarak)
      const urlParams = new URLSearchParams(window.location.search);
      const runId = urlParams.get('runId');
      
      if (!runId) {
        console.log('‚ö†Ô∏è RunId bulunamadƒ±, manuel baƒülantƒ± gerekli');
        return;
      }

      console.log(`üì± RunId ile baƒülanƒ±yor: ${runId}`);
      
      // Backend'e mobile_connect isteƒüi g√∂nder
      const response = await fetch('http://localhost:3001/api/mobile-connect', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ runId })
      });

      if (response.ok) {
        console.log('‚úÖ Mobil uygulama backend\'e baƒülandƒ±');
        toast.success('‚úÖ Backend\'e baƒülandƒ±');
        
        // mobile_ready isteƒüi g√∂nder
        await fetch('http://localhost:3001/api/mobile-ready', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ runId })
        });
        
        console.log('‚úÖ Mobil test hazƒ±r durumu bildirildi');
      } else {
        console.error('‚ùå Backend baƒülantƒ±sƒ± ba≈üarƒ±sƒ±z:', response.status);
        toast.error('‚ùå Backend baƒülantƒ±sƒ± ba≈üarƒ±sƒ±z');
      }
    } catch (error) {
      console.error('‚ùå Backend baƒülantƒ± hatasƒ±:', error);
      toast.error('‚ùå Backend baƒülantƒ± hatasƒ±');
    }
  };

  // Queue sayƒ±sƒ±nƒ± g√ºncelle
  const updateQueueCount = async () => {
    const count = await offlineQueue.getQueueLength();
    setQueueCount(count);
  };

  // Offline queue'yu senkronize et
  const syncOfflineQueue = async () => {
    if (!isOnline) return;
    
    try {
      setIsSyncing(true);
      const synced = await offlineQueue.syncWithServer();
      if (synced > 0) {
        toast.success(`‚úÖ ${synced} barkod g√∂nderildi`);
        updateQueueCount();
      }
    } catch (error) {
      toast.error('‚ùå ƒ∞≈ülem tamamlanamadƒ±. Tekrar deneyin.');
      console.error('Sync error:', error);
    } finally {
      setIsSyncing(false);
    }
  };

  return (
    <Router>
      <div className="mobile-app">
        {/* Header */}
        <header className="mobile-header">
          <div className="header-content">
            <div className="header-left">
              <h1 className="app-title" data-testid="app-title">Hava Kargo Test & Sim√ºlasyon Merkezi</h1>
              <p className="app-subtitle">Dijital ƒ∞kiz Tabanlƒ± Test Sistemi</p>
            </div>
            <StatusChip 
              status="online" 
              isOnline={isOnline} 
              isSyncing={isSyncing} 
            />
          </div>
          
          {/* Offline Queue Indicator */}
          {queueCount > 0 && (
            <div className="queue-indicator">
              <span className="queue-count">{queueCount}</span>
              <span className="queue-label">Barkod Kuyrukta</span>
              <button 
                className="sync-button"
                onClick={syncOfflineQueue}
                disabled={!isOnline || isSyncing}
                title="Kuyruƒüu ƒ∞≈üle"
              >
                <FaSync className={`sync-icon ${isSyncing ? 'spinning' : ''}`} />
                {isSyncing ? 'ƒ∞≈üleniyor...' : 'ƒ∞≈üle'}
              </button>
            </div>
          )}
        </header>

        {/* Main Content */}
        <main className="mobile-main">
          <Routes>
            <Route path="/" element={<SyncedBarcodeScanner offlineQueue={offlineQueue} onUpdate={updateQueueCount} />} />
            <Route path="/awb" element={<AwbAcceptance offlineQueue={offlineQueue} />} />
            <Route path="/uld" element={<UldLoading offlineQueue={offlineQueue} />} />
          </Routes>
        </main>

        {/* Bottom Navigation */}
        <MobileNavigation queueCount={queueCount} />

        {/* Toast Container */}
        <ToastContainer
          position="bottom-center"
          autoClose={3000}
          hideProgressBar={false}
          newestOnTop={false}
          closeOnClick
          rtl={false}
          pauseOnFocusLoss
          draggable
          pauseOnHover
          theme="light"
        />
      </div>
    </Router>
  );
};

export default App;
